    public void save(OutputStream os, Database db) {
        Writer stream = new OutputStreamWriter(os);
        try {
            for (Iterator idb = db.getTables().iterator(); idb.hasNext();) {
                Table tuples = (Table) idb.next();
                for (Iterator i = tuples.getAttributes().iterator(); i.hasNext();) {
                    Attribute attr = (Attribute) i.next();
                    stream.write("(var '" + attr.getName() + " '(");
                    for (Iterator j = attr.getValues().iterator(); j.hasNext();) {
                        stream.write(j.next().toString());
                        if (j.hasNext()) stream.write(" ");
                    }
                    stream.write("))" + ln + ln);
                }

                for (Iterator i = tuples.getTuples().iterator(); i.hasNext();) {
                    Tuple tuple = (Tuple) i.next();
                    stream.write("(");
                    for (Iterator j = tuple.getValues().iterator(); j.hasNext();) {
                        stream.write(j.next().toString());
                        if (j.hasNext()) stream.write(" ");
                    }
                    stream.write(")" + ln);
                }
            }
            stream.flush();
            stream.close();
        } catch (Exception e) {
            throw new RuntimeException(e);
        }
    }

